package com.papco.sundar.papcojoballotment.screens.poolimport com.papco.sundar.papcojoballotment.utility.Durationclass JobsSelection {    private var selectionList:HashMap<String,Int> = HashMap()    private var totalDuration=Duration()    fun add(key:String,position: Int,duration: Duration){        if(!selectionList.containsKey(key))            totalDuration+=duration        selectionList[key]=position    }    fun remove(key:String,duration: Duration){        if(selectionList.containsKey(key)){            selectionList.remove(key)            totalDuration-=duration        }    }    fun keys():MutableSet<String>{        return selectionList.keys    }    fun get(key:String):Int?{        return selectionList[key]    }    fun isEmpty():Boolean{        return selectionList.isEmpty()    }    fun contains(key:String):Boolean{        return selectionList.containsKey(key)    }    fun getDuration():Duration{        return totalDuration    }    fun clear(){        selectionList.clear()        totalDuration= Duration()    }}